import ProgressBar from 'progressbar.js'; //get quotes from the database
//we will be getting 100 quotes randomly from the db
//we display  a quote every 7 secs
//at each display iteration, if quotes available is less than 20, we fetch another 100

export default {
  props: ["webserver_url"],
  data: function data() {
    return {
      available_quotes: [],
      quote: '',
      quotes_change_interval: 7000,
      //in ms
      show_quote: true,
      quotesBarWidth: 0,
      count: 0,
      ongoingTimeout: 0,
      progressbar: null
    };
  },
  computed: {},
  mounted: function mounted() {
    this.progressbar = new ProgressBar.Circle("#progressbar", {
      strokeWidth: 9,
      easing: 'linear',
      duration: 1000,
      color: 'grey',
